// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.23;

/// @notice output format struct for `_getWithdrawalStatus()` mEthod
struct WithdrawalRequestStatus {
    /// @notice stEth token amount that was locked on withdrawal queue for this request
    uint256 amountOfStEth;
    /// @notice amount of stEth shares locked on withdrawal queue for this request
    uint256 amountOfShares;
    /// @notice address that can claim or transfer this request
    address owner;
    /// @notice timestamp of when the request was created, in seconds
    uint256 timestamp;
    /// @notice true, if request is finalized
    bool isFinalized;
    /// @notice true, if request is claimed. Request is claimable if (isFinalized && !isClaimed)
    bool isClaimed;
}

interface IWithdrawalQueueERC721 {
    function claimWithdrawals(uint256[] calldata _requestIds, uint256[] calldata _hints) external;
    function findCheckpointHints(
        uint256[] calldata _requestIds,
        uint256 _firstIndex,
        uint256 _lastIndex
    )
        external
        view
        returns (uint256[] memory hintIds);
    function finalize(uint256 _lastRequestIdToBeFinalized, uint256 _maxShareRate) external payable;
    function getLastCheckpointIndex() external view returns (uint256);
    function getWithdrawalStatus(uint256[] calldata _requestIds)
        external
        view
        returns (WithdrawalRequestStatus[] memory statuses);
    function requestWithdrawals(
        uint256[] calldata _amounts,
        address _owner
    )
        external
        returns (uint256[] memory requestIds);
}

src/StakingManager.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.23;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { Ownable2StepUpgradeable } from "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IStakingStrategy } from "./interfaces/IStakingStrategy.sol";

/**
 * @notice The purpose of this contract is solely to take in assets and send them to strategies.
 * Upon withdrawal, all assets go to the depositor. The depositor needs to be set after deployment
 */
contract StakingManager is Ownable2StepUpgradeable {
    using SafeERC20 for IERC20;

    mapping(address => IStakingStrategy[]) public strategies;
    mapping(address => uint256) public activeStrategyIndex;
    address public staker;
    address public depositor;
    bool public autoStake;
    address public constant ETH_ADDRESS = address(0xEeeeeEeeeEeEeeEeEeEeeEEEeeeeEeeeeeeeEEeE);
    uint256[50] private __gap;

    event Stake(address indexed asset, uint256 amount);
    event Withdraw(address indexed asset, uint256 amount);
    event SetStaker(address staker);
    event SetAutoStake(bool autoStake);
    event AddStrategy(address indexed asset, IStakingStrategy indexed strategy);
    event SetActiveStrategy(address indexed asset, uint256 index);
    event RemoveStrategy(address indexed asset, IStakingStrategy indexed strategy, uint256 withdrawnAmount);

    error OnlyStaker(address sender);
    error TransferFailed(bytes data);

    modifier onlyStaker() {
        if (msg.sender != staker) {
            revert OnlyStaker(msg.sender);
        }
        _;
    }

    function initialize(address _owner) external initializer {
        __Ownable_init(_owner);
    }

    /// -------------------------------- 📝 Staker Functions 📝 --------------------------------
    function stake(address asset, uint256 amount) external payable onlyStaker {
        _stakeEth(msg.value);
        emit Stake(asset, amount);
    }

    function _stakeEth(uint256 amount) internal {
        IStakingStrategy strategy = getActiveStrategy(ETH_ADDRESS);
        strategy.deposit{ value: amount }(amount);
    }

    function withdraw(uint256 amount) external onlyStaker {
        _withdrawEth(amount);
    }

    function _withdrawEth(uint256 amount) internal {
        IStakingStrategy strategy = getActiveStrategy(ETH_ADDRESS);
        uint256 withdrawnAmount;
        if (address(strategy) != address(0)) {
            withdrawnAmount = strategy.withdraw(amount);
        } else {
            withdrawnAmount = amount > address(this).balance ? address(this).balance : amount;
        }
        (bool success, bytes memory data) = staker.call{ value: withdrawnAmount }("");
        if (!success) revert TransferFailed(data);
        emit Withdraw(ETH_ADDRESS, amount);
    }

    /// ---------------------------------- 🔓 Admin Functions 🔓 ----------------------------------
    function setStaker(address _staker) external onlyOwner {
        staker = _staker;
        emit SetStaker(_staker);
    }

    function setAutoStake(bool _autoStake) external onlyOwner {
        autoStake = _autoStake;
        emit SetAutoStake(_autoStake);
    }

    function addStrategy(address asset, IStakingStrategy strategy) external onlyOwner {
        strategies[asset].push(strategy);
        emit AddStrategy(asset, strategy);
    }

    function setActiveStrategy(address asset, uint256 index) external onlyOwner {
        activeStrategyIndex[asset] = index;
        emit SetActiveStrategy(asset, index);
    }

    function removeStrategy(address asset, uint256 index) external onlyOwner {
        IStakingStrategy strategy = strategies[asset][index];
        uint256 withdrawnAmount = strategy.withdraw(strategy.underlyingAssetAmount());
        uint256 lastIndex = strategies[asset].length - 1;
        strategies[asset][index] = strategies[asset][lastIndex];
        strategies[asset].pop();
        if (activeStrategyIndex[asset] == index) {
            activeStrategyIndex[asset] = 0;
        }
        emit RemoveStrategy(asset, strategy, withdrawnAmount);
    }

    /// --------------------------------- 🔎 View Functions 🔍 ---------------------------------
    function getActiveStrategy(address asset) public view returns (IStakingStrategy) {
        return strategies[asset][activeStrategyIndex[asset]];
    }

    function getAssetTotal(address asset) external view returns (uint256 total) {
        for (uint256 i = 0; i < strategies[asset].length; i++) {
            IStakingStrategy strategy = strategies[asset][i];
            total += strategy.underlyingAssetAmount();
        }
    }

    receive() external payable { }
}

src/strategies/EthStrategy.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.8.23;

import { Ownable2StepUpgradeable } from "@openzeppelin/contracts-upgradeable/access/Ownable2StepUpgradeable.sol";

import { IStakingStrategy } from "../interfaces/IStakingStrategy.sol";
import { LIDO, LIDO_WITHDRAWAL_ERC721 } from "../Constants.sol";

contract EthStrategy is IStakingStrategy, Ownable2StepUpgradeable {
    address public stakingManager;
    bool public autoStake;
    uint256[50] private __gap;

    event EthStaked(uint256 amount);
    event EthWithdrawn(uint256 amount);
    event RequestedLidoWithdrawals(uint256[] requestIds, uint256[] amounts);
    event ClaimedLidoWithdrawals(uint256[] requestIds);
    event SetStakingManager(address stakingManager);
    event SetAutoStake(bool autoStake);

    error InsufficientFunds();
    error TransferFailed(bytes data);
    error OnlyStakingManager(address sender);
    error RequestIdsMustBeSorted();

    modifier onlyStakingManager() {
        if (msg.sender != stakingManager) revert OnlyStakingManager(msg.sender);
        _;
    }

    function initialize(address _owner, address _stakingManager) external initializer {
        stakingManager = _stakingManager;
        autoStake = true;
        __Ownable_init(_owner);
    }

    /// -------------------------------- 📝 External Functions 📝 --------------------------------
    function deposit(uint256 amount) external payable onlyStakingManager {
        if (!autoStake) return;
        if (amount > address(this).balance) revert InsufficientFunds();
        LIDO.submit{ value: amount }(address(0));
        emit EthStaked(amount);
    }

    function withdraw(uint256 amount) external onlyStakingManager returns (uint256 withdrawnAmount) {
        uint256 balance = address(this).balance;
        if (amount > balance) {
            withdrawnAmount = balance;
        } else {
            withdrawnAmount = amount;
        }
        (bool success, bytes memory data) = stakingManager.call{ value: withdrawnAmount }("");
        if (!success) revert TransferFailed(data);
        emit EthWithdrawn(withdrawnAmount);
        return withdrawnAmount;
    }

    /// ---------------------------------- 🔓 Admin Functions 🔓 ----------------------------------
    function ownerDeposit(uint256 amount) external payable onlyOwner {
        if (amount > address(this).balance) revert InsufficientFunds();
        LIDO.submit{ value: amount }(address(0));
        emit EthStaked(amount);
    }

    function ownerWithdraw(uint256 amount) external onlyOwner returns (uint256 withdrawnAmount) {
        (bool success, bytes memory data) = stakingManager.call{ value: amount }("");
        if (!success) revert TransferFailed(data);
        emit EthWithdrawn(amount);
        return amount;
    }

    function requestLidoWithdrawal(uint256[] calldata amounts)
        external
        onlyOwner
        returns (uint256[] memory requestIds)
    {
        uint256 total;
        for (uint256 i; i < amounts.length; ++i) {
            total += amounts[i];
        }
        LIDO.approve(address(LIDO_WITHDRAWAL_ERC721), total);
        requestIds = LIDO_WITHDRAWAL_ERC721.requestWithdrawals(amounts, address(this));
        emit RequestedLidoWithdrawals(requestIds, amounts);
    }

    function claimLidoWithdrawals(uint256[] calldata requestIds) external onlyOwner {
        // Check if requestIds is sorted
        for (uint256 i = 0; i < requestIds.length - 1; i++) {
            if (requestIds[i] > requestIds[i + 1]) revert RequestIdsMustBeSorted();
        }
        uint256 lastCheckpointIndex = LIDO_WITHDRAWAL_ERC721.getLastCheckpointIndex();
        uint256[] memory _hints = LIDO_WITHDRAWAL_ERC721.findCheckpointHints(requestIds, 1, lastCheckpointIndex);
        LIDO_WITHDRAWAL_ERC721.claimWithdrawals(requestIds, _hints);
        emit ClaimedLidoWithdrawals(requestIds);
    }

    function setStakingManager(address _stakingManager) external onlyOwner {
        stakingManager = _stakingManager;
        emit SetStakingManager(_stakingManager);
    }

    function setAutoStake(bool _autoStake) external onlyOwner {
        autoStake = _autoStake;
        emit SetAutoStake(_autoStake);
    }

    /// --------------------------------- 🔎 View Functions 🔍 ---------------------------------
    function underlyingAssetAmount() external view returns (uint256) {
        return address(this).balance + LIDO.balanceOf(address(this));
    }

    receive() external payable { }
}

